import React, { memo } from 'react'
import { NavLink } from 'react-router-dom';

import styles from '../styles/Nav.module.scss'
import homeStyles from '../styles/Home.module.scss'

/**
 * Typing animation for your name - https://github.com/ssbeefeater/react-typed
 */

function Nav(){
    const handleScroll = (sectionId) => {
        const element = document.getElementById(sectionId);
        if (element) {
            window.scrollTo({
                top: element.offsetTop,
                behavior: 'smooth'
            });
        }
    };

    return(
        <header>
            <nav>
                <h1>Jonathan Ramon</h1>
                <ul>
                    <li><NavLink end style={({ isActive }) => ({ backgroundColor: isActive ? '#FF1342' : 'black' })} className={styles.linkStyles} to={homeStyles.aboutMeContainer} onClick={() => handleScroll( homeStyles.aboutMeContainer )}>Home</NavLink></li>
                    <li><NavLink style={({ isActive }) => ({ backgroundColor: isActive ? '#FF1342' : 'black' })} className={styles.linkStyles} to={homeStyles.projectsContainer} onClick={() => handleScroll( homeStyles.projectsContainer )}>Projects</NavLink></li>
                    <li><NavLink style={({ isActive }) => ({ backgroundColor: isActive ? '#FF1342' : 'black' })} className={styles.linkStyles} to={homeStyles.contactMeContainer} onClick={() => handleScroll( homeStyles.contactMeContainer )}>Contact</NavLink></li>
                </ul>
            </nav>
        </header>
    )
};

export default memo(Nav);
