import  React, { useState } from 'react';
import { styled } from '@mui/material/styles';
import ArrowForwardIosSharpIcon from '@mui/icons-material/ArrowForwardIosSharp';
import MuiAccordion from '@mui/material/Accordion';
import MuiAccordionSummary from '@mui/material/AccordionSummary';
import MuiAccordionDetails from '@mui/material/AccordionDetails';
import Typography from '@mui/material/Typography';
import styles from '../styles/Projects.module.scss';

const Accordion = styled(MuiAccordion)(({ theme }) => ({
    display: 'flex',
    flexDirection: 'column', // Each accordion as a column
    border: `1px solid ${theme.palette.divider}`,
    '&:not(:last-child)': {
        borderBottom: `1px solid ${theme.palette.divider}`, // Add a bottom border for separation
    },
    '&::before': {
        display: 'none',
    },
}));

const AccordionSummary = styled(MuiAccordionSummary)(({ theme }) => ({
    backgroundColor: theme.palette.action.hover,
    flexDirection: 'row', // Keep content inline
    alignItems: 'center',
    justifyContent: 'center',
    '& .MuiAccordionSummary-expandIconWrapper.Mui-expanded': {
        transform: 'rotate(90deg)', // Rotate icon for expansion
    },
}));

const AccordionDetails = styled(MuiAccordionDetails)(({ theme }) => ({
    padding: theme.spacing(2),
    borderTop: '1px solid rgba(0, 0, 0, 0.125)',
}));

export default function ColumnAccordion() {
    const [expanded, setExpanded] = useState('panel1');

    const handleChange = (panel) => (event, isExpanded) => {
        setExpanded(isExpanded ? panel : false);
    };

    return (
        <div
            style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(3, 1fr)', // Create 3 equal-width columns
                gap: '16px', // Add spacing between columns
                padding: '16px',
            }}
        >
            <Accordion
                expanded={expanded === 'panel1'}
                onChange={handleChange('panel1')}
            >
                <AccordionSummary
                    aria-controls="panel1d-content"
                    id="panel1d-header"
                    expandIcon={<ArrowForwardIosSharpIcon />}
                >
                    <Typography>Column 1</Typography>
                </AccordionSummary>
                <AccordionDetails>
                    <Typography>
                        Content for column 1. Lorem ipsum dolor sit amet, consectetur
                        adipiscing elit.
                    </Typography>
                </AccordionDetails>
            </Accordion>
            <Accordion
                expanded={expanded === 'panel2'}
                onChange={handleChange('panel2')}
            >
                <AccordionSummary
                    aria-controls="panel2d-content"
                    id="panel2d-header"
                    expandIcon={<ArrowForwardIosSharpIcon />}
                >
                    <Typography>Column 2</Typography>
                </AccordionSummary>
                <AccordionDetails>
                    <Typography>
                        Content for column 2. Suspendisse malesuada lacus ex.
                    </Typography>
                </AccordionDetails>
            </Accordion>
            <Accordion
                expanded={expanded === 'panel3'}
                onChange={handleChange('panel3')}
            >
                <AccordionSummary
                    aria-controls="panel3d-content"
                    id="panel3d-header"
                    expandIcon={<ArrowForwardIosSharpIcon />}
                >
                    <Typography>Column 3</Typography>
                </AccordionSummary>
                <AccordionDetails>
                    <Typography>
                        Content for column 3. Sed malesuada blandit leo lobortis eget.
                    </Typography>
                </AccordionDetails>
            </Accordion>
        </div>
    );
}
