import React, { memo } from 'react'
import { Link } from 'react-scroll'

import styles from '../styles/Nav.module.scss'

/**
 * Typing animation for your name - https://github.com/ssbeefeater/react-typed
 */

function Nav(){

    const handleClick = (sectionId) => {
        const element = document.getElementById(sectionId);
        if (element){
            window.scrollTo({
                top: element.offsetTop,
                behavior: 'smooth',
            });
        }
    }

    return(
        <header>
            <nav>
                <h1>Jonathan Ramon</h1>
                <ul>
                    <li><Link end 
                    style={({isActive}) => ({backgroundColor: isActive ? '#FF1342': 'black'})} 
                    className={styles.linkStyles} 
                    to="portfolio-proj/home"
                    >
                     Home
                        </Link>
                    </li>
                    <li><Link style={({isActive}) => ({backgroundColor: isActive ? '#FF1342': 'black'})} className={styles.linkStyles} to="portfolio-proj/projects">Projects</Link></li>
                    <li><Link style={({isActive}) => ({backgroundColor: isActive ? '#FF1342': 'black'})} className={styles.linkStyles} to="portfolio-proj/contact">Contact</Link></li>
                </ul>
            </nav>
        </header>
    )
};

export default memo(Nav);
